    COMMENT '生成唯一标识'
begin

	declare upd_res tinyint(1) default 0;

	if table_name = 'administrator' then

		update toocms_administrator set unique_code=concat(floor(100+(rand()*899)),new_id) where (id=new_id);

	elseif table_name = 'kp' then

